/**
 * @preserve
 * Fast, destructive implemetation of Liang-Barsky line clipping algorithm.
 * It clips a 2D segment by a rectangle.
 * @author Alexander Milevski <info@w8r.name>
 * @license MIT
 */
/**
 * Endpoint
 * @type {Array<number>}
 */
export declare type Point = [number, number];
/**
 * Rectangle
 * @type {Array<number>}
 */
export declare type BoundingBox = [number, number, number, number];
/**
 * @param  {Point} a
 * @param  {Point} b
 * @param  {BoundingBox} box [xmin, ymin, xmax, ymax]
 * @param  {Point?} [da]
 * @param  {Point?} [db]
 * @return {number}
 */
export default function clip(a: Point, b: Point, box: BoundingBox, da?: Point, db?: Point): 1 | 0;
